$(function(){
	//第一步验证手机号
	$("#item-phone").change(function(){
		var reg1=/^1(3|4|5|7|8|9)\d{9}$/;
		var val = $("#item-phone").val();
		if(reg1.test(val)){
			console.log("格式正确");
			$("#worn-tel").css("display","none")
			$("#reg-form #form-item1 span").css({"background":"url(../imgs/line.png) no-repeat -110px -160px"})
		}else{
			$("#worn-tel").css("display","block")
			$("#form-item1 span").css({"background":"url(../imgs/line.png) no-repeat -80px -160px"})
			}
		$("#next").click(function(){
		var txt1= $("#random").text();
		var txt2= $("#inner_txt").val();
		console.log(txt1,txt2)
		if(txt1==txt2&&reg1.test(val)){
			console.log(txt1==txt2&&reg1.test(val))
			$("#reg-form").css({"display":"none"})
			$("#reg-detail").css({"display":"block"})
			$(".pro-line1").css({"background":"url(imgs/line.png) no-repeat 0px -128px"})
			$(".step-index2").css({"background":"url(imgs/line.png) no-repeat 0px -200px"})                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
		}else{
			$("#inner-txt").val("")
		}
	})
		
	})
	$("#check").click(function(){
		$("#random-wrap").css({"display":"block"});
		 function rand(min,max) {
        return Math.floor(Math.random()*(max-min))+min;
    }
		$("#random").text(rand(1000,9999))
	})
	
			
	//第二步注册			
	$("#reg-btn").click(function(){
		//console.log("222")
		$.post("http://47.104.244.134:8080/usersave.do",{
				username:$(".reg-inner1 input").val(),
				password:$(".reg-inner2 input").val(),
				email:$(".reg-inner3 input").val(),
				sex:$(".reg-inner4 input").val()
			}).done(data=>{
				console.log(data);
				if(data.code==0){
					$(".pro-line2").css({"background":"url(imgs/line.png) no-repeat 0px -128px"})
					$(".step-index3").css({"background":"url(imgs/line.png) no-repeat 0px -200px"})
				}
				
			})
	})
	//验证用户名
	$(".reg-inner1 input").blur(function(){
		var name = $(".reg-inner1 input").val()
		$.get("http://47.104.244.134:8080/username.do",{username:name}).done(data=>{
				console.log(data);
				if(data.code==1){
				console.log("aaa")
				$("#worn-name").css("display","none")
				$(".reg-inner1 span").css({"background":"url(../imgs/line.png) no-repeat -110px -160px"})	
				}else{
				$("#worn-name").css("display","block")
				$(".reg-inner1 span").css({"background":"url(../imgs/line.png) no-repeat -80px -160px"})	
				}
			})
			
	})
	//验证密码是否为空
	$(".reg-inner2 input").change(function(){
		$(".reg-inner2 span").css({"background":"url(../imgs/line.png) no-repeat -110px -160px"})	
	})
	//验证邮箱
	$(".reg-inner3 input").change(function(){
		var reg2=/^\w+@\w+(\.\w+)+$/
		var val = this.value;
		$.get("http://47.104.244.134:8080/useremail.do",{email:$(".reg-inner3 input").val()}).done(data=>{
			console.log(data)
		})
		if(reg2.test(val)){
			console.log("格式正确");
			$("#inner-worn").css({"display":"none"})
			$(".reg-inner3 span").css({"background":"url(../imgs/line.png) no-repeat -110px -160px"})	
		}else{
			$("#inner-worn").css({"display":"block"})
		}		
	})
	//保证输入框中是男或者女
	$(".reg-inner4 input").change(function(){
		if($(".reg-inner4 input").val()!="男"&&$(".reg-inner4 input").val()!="女"){
			$("#inner-worn1").css({"display":"block"})
		}
		if($(".reg-inner4 input").val()=="男"||$(".reg-inner4 input").val()=="女"){
			$("#inner-worn1").css({"display":"none"})
			$(".reg-inner4 span").css({"background":"url(../imgs/line.png) no-repeat -110px -160px"})	
		}
	})
	
})

